mapper.select:
	select   from dtp_resource
	where tableName = #{tableName}
	and columnName = #{columnName}
	and columnValue = #{columnValue}

    and txid = #{txid}

mapper.delete:
	delete from dtp_resource
	where tableName = #{tableName}
	and columnName = #{columnName}
	and columnValue = #{columnValue}

    and txid = #{txid}
