mapper.select:
select *   from ecm_param      


    PARA_CODE like '${baseParam.paraCode}%'


    AND PARA_NAME like '%${baseParam.paraName}%'


    AND IS_VALIDATE = #{baseParam.isValidate,jdbcType=VARCHAR}


    AND PARA_PARENT_ID = #{baseParam.paraParentId,jdbcType=VARCHAR}


order by PARA_CODE

select *   from ecm_param


    AND PARA_NAME = #{paraName,jdbcType=VARCHAR}


    AND IS_VALIDATE = #{isValidate,jdbcType=VARCHAR}


    AND PARA_PARENT_ID = #{paraParentId,jdbcType=VARCHAR}


order by PARA_CODE
