mapper.select:
select *
from ecm_app_valrule 


   AND  APP_UPG_ID = #{appUpgId,jdbcType=INTEGER}


   AND APP_VALRULE_TYPE = #{appValruleType,jdbcType=VARCHAR}

select a.* ,b.*,c.ROUTER_COND_NAME,c.ROUTER_COND_VAL,d.SER_MTD_CNM,d.SER_MTD_ENM,e.APP_SER_CLSNM,e.APP_SER_NAME,g.ROUTER_COL_CN
from    ecm_app_valrule a ,ECM_APP_UPGINFO f,
        ECM_SERV_RULE b LEFT  JOIN ECM_ROUTER_COND c  on b.ROUTER_COND_ID  =c.ROUTER_COND_ID
        LEFT  JOIN ECM_ROUTER_COL g on c.ROUTER_COL_ID=g.ROUTER_COL_ID,ECM_SER_MTDINFO d ,ECM_APP_SERINFO e
where  b.SER_MTD_ID  =d.SER_MTD_ID AND d. APP_SER_ID =e.APP_SER_ID AND APP_SER_TYPE=#{appSerType,jdbcType=VARCHAR}  

  AND  f.APP_UPG_ID = #{appUpgId,jdbcType=INTEGER}  AND  f.APP_UPG_ID =a.APP_UPG_ID AND a.SERV_RULE_ID=b.SERV_RULE_ID  


  AND  a.APP_VALRULE_TYPE = #{appValruleType,jdbcType=INTEGER}
