mapper.select:
select *
from ecm_appinst_deploy
where APP_INTANT_ID = #{appIntantId,jdbcType=INTEGER}  AND APP_VER_ID = #{appVerId,jdbcType=INTEGER}

select   a.APP_NAME,a.APP_PATH,a.APP_PORT,a.APP_WORK,b.SER_IP,c.APP_INTANT_NAME,c.APP_INTANT_VER,d.APP_DEPLOY_DATE,e.USER_NAME
from  ecm_app_info a ,ecm_server_info b,ecm_app_intant c,ecm_appinst_deploy d,WEB_USER e
where d.APP_INTANT_ID=c.APP_INTANT_ID and c.APP_ID=a.APP_ID and c.SER_ID=b.SER_ID and d.APP_DEPLOY_USERID =e.USER_ID

 AND a.APP_ID= #{baseParam.appId ,jdbcType=INTEGER}


  AND d.APP_DEPLOY_USERID = #{baseParam.userId,jdbcType=INTEGER}



AND 
d.APP_DEPLOY_DATE >= #{baseParam.startTime,jdbcType=VARCHAR} and d.APP_DEPLOY_DATE<= #{baseParam.endTime,jdbcType=VARCHAR}



order by d.APP_DEPLOY_DATE  DESC

mapper.update:
  update ecm_appinst_deploy


      APP_DEPLOY_DATE = #{appDeployDate,jdbcType=VARCHAR},


      APP_DEPLOY_USERID = #{appDeployUserid,jdbcType=INTEGER}


where APP_DEPLOY_ID = #{appDeployId,jdbcType=INTEGER}
