mapper.select:
select a.APP_NAME,a.APP_PATH,a.APP_PORT,b.SER_IP,c.APP_INTANT_NAME,c.APP_INTANT_VER,d.ECM_APPOPER_TIME, d.ECM_APPOPER_TYPE,f.USER_NAME
from  ecm_app_info a ,ecm_server_info b,ecm_app_intant c,ecm_appoper_info d,ecm_param e,web_user f
where d.APP_INTANT_ID=c.APP_INTANT_ID and c.APP_ID=a.APP_ID and c.SER_ID=b.SER_ID and d.ECM_APPOPER_USERID=f.USER_ID and d.ECM_APPOPER_TYPE=e.PARA_CODE

 AND a.APP_ID= #{baseParam.appId ,jdbcType=INTEGER}


 AND d.ECM_APPOPER_TYPE= #{baseParam.paraCode ,jdbcType=VARCHAR}


  AND d.ECM_APPOPER_USERID = #{baseParam.userId,jdbcType=INTEGER}



AND 
d.ecm_appoper_time >= #{baseParam.startTime,jdbcType=VARCHAR} and d.ECM_APPOPER_TIME<= #{baseParam.endTime,jdbcType=VARCHAR}



order by d.ECM_APPOPER_TIME  DESC

mapper.update:
update ecm_appoper_info


    APP_INTANT_ID = #{appIntantId,jdbcType=INTEGER},


    ECM_APPOPER_TYPE = #{ecmAppoperType,jdbcType=VARCHAR},


    ECM_APPOPER_TIME = #{ecmAppoperTime,jdbcType=VARCHAR},


    ECM_APPOPER_USERID = #{ecmAppoperUserid,jdbcType=INTEGER}


where ECM_APPOPER_ID = #{ecmAppoperId,jdbcType=INTEGER}
