mapper.select:
select distinct a.*,b.ROUTER_COND_OPER,b.ROUTER_COND_VAL,c.SER_MTD_ENM,e.ROUTER_COL_CDN,e.ROUTER_COL_TYPE,f.APP_SER_CLSNM
from ECM_SERV_RULE a LEFT  JOIN ECM_ROUTER_COND b on a.ROUTER_COND_ID = b.ROUTER_COND_ID 
LEFT  JOIN ECM_ROUTER_COL e on b.ROUTER_COL_ID = e.ROUTER_COL_ID,ECM_SER_MTDINFO c,ECM_APP_VALRULE d,ECM_APP_SERINFO f,ECM_APP_UPGINFO g
where  a.SER_MTD_ID = c.SER_MTD_ID and a.SERV_RULE_ID = d.SERV_RULE_ID and  c.APP_SER_ID = f.APP_SER_ID and d.APP_UPG_ID = g.APP_UPG_ID   

   AND g.APP_ID = #{appId,jdbcType=VARCHAR}


  AND g.APP_UPG_STATUS = #{appUpgStatus,jdbcType=VARCHAR}


  AND d.APP_VALRULE_TYPE = #{appValruleType,jdbcType=VARCHAR}
