mapper.select:
select *
from gl_prod_rule
where PROD_TYPE = #{prodType,jdbcType=VARCHAR}

  AND CCY = #{ccy,jdbcType=VARCHAR}


  AND CLIENT_TYPE = #{clientType,jdbcType=VARCHAR}


  AND ACCOUNTING_STATUS = #{accountingStatus,jdbcType=VARCHAR}


  AND SOURCE_TYPE = #{sourceType,jdbcType=VARCHAR}


  AND SYS_NAME = #{sysName,jdbcType=VARCHAR}


  AND TRAN_EVENT_TYPE = #{tranEventType,jdbcType=VARCHAR}


  AND TRAN_TYPE = #{tranType,jdbcType=VARCHAR}

mapper.delete:
delete from gl_prod_rule
where PROD_TYPE = #{prodType,jdbcType=VARCHAR}

  AND CCY = #{ccy,jdbcType=VARCHAR}


  AND CLIENT_TYPE = #{clientType,jdbcType=VARCHAR}


  AND ACCOUNTING_STATUS = #{accountingStatus,jdbcType=VARCHAR}


  AND SOURCE_TYPE = #{sourceType,jdbcType=VARCHAR}


  AND SYS_NAME = #{sysName,jdbcType=VARCHAR}


  AND TRAN_EVENT_TYPE = #{tranEventType,jdbcType=VARCHAR}


  AND TRAN_TYPE = #{tranType,jdbcType=VARCHAR}

mapper.update:
update gl_prod_rule


    ACCOUNTING_NO = #{accountingNo,jdbcType=VARCHAR},


    CUSTOM_RULE = #{customRule,jdbcType=VARCHAR},


    ACCOUNTING_DESC = #{accountingDesc,jdbcType=VARCHAR}


where PROD_TYPE = #{prodType,jdbcType=VARCHAR}
AND CCY = #{ccy,jdbcType=VARCHAR}
AND CLIENT_TYPE = #{clientType,jdbcType=VARCHAR}
AND ACCOUNTING_STATUS = #{accountingStatus,jdbcType=VARCHAR}
AND SOURCE_TYPE = #{sourceType,jdbcType=VARCHAR}
AND SYS_NAME = #{sysName,jdbcType=VARCHAR}
AND TRAN_EVENT_TYPE = #{tranEventType,jdbcType=VARCHAR}
AND TRAN_TYPE = #{tranType,jdbcType=VARCHAR}
