mapper.select:
select *
from irl_prod_int
where PROD_TYPE = #{prodType,jdbcType=VARCHAR}

  AND INT_CLASS = #{intClass,jdbcType=VARCHAR}


  AND EVENT_TYPE = #{eventType,jdbcType=VARCHAR}

select *
from irl_prod_int
where '1' = '1'

  AND PROD_TYPE = #{prodType,jdbcType=VARCHAR}


  AND INT_CLASS = #{intClass,jdbcType=VARCHAR}


  AND EVENT_TYPE = #{eventType,jdbcType=VARCHAR}

mapper.delete:
delete from irl_prod_int
where PROD_TYPE = #{prodType,jdbcType=VARCHAR}

  AND INT_CLASS = #{intClass,jdbcType=VARCHAR}


  AND EVENT_TYPE = #{eventType,jdbcType=VARCHAR}
