mapper.select:
select *
from mb_attr_value
where ATTR_KEY = #{attrKey,jdbcType=VARCHAR}

  AND ATTR_VALUE = #{attrValue,jdbcType=VARCHAR}

AND COLUMN_STATUS !='W'

select *
from mb_attr_value
where ATTR_KEY = #{attrKey,jdbcType=VARCHAR}

  AND ATTR_VALUE = #{attrValue,jdbcType=VARCHAR}

AND COLUMN_STATUS !='W'

select * from mb_attr_value
where  ATTR_KEY = #{attrKey,jdbcType=VARCHAR}
AND COLUMN_STATUS !='W'

mapper.delete:
delete from mb_attr_value
where ATTR_KEY = #{attrKey,jdbcType=VARCHAR}

    AND ATTR_VALUE = #{attrValue,jdbcType=VARCHAR}

mapper.update:
update mb_attr_value


    VALUE_DESC = #{valueDesc,jdbcType=VARCHAR},


    REF_TABLE = #{refTable,jdbcType=VARCHAR},


    REF_CONDITION = #{refCondition,jdbcType=VARCHAR},


    REF_COLUMNS = #{refColumns,jdbcType=VARCHAR},


    COMPANY = #{company,jdbcType=VARCHAR}


where ATTR_KEY = #{attrKey,jdbcType=VARCHAR}
AND ATTR_VALUE = #{attrValue,jdbcType=VARCHAR}

mapper.select:
select VALUE_DESC from mb_attr_value
WHERE ATTR_KEY = #{attrKey,jdbcType=VARCHAR}
AND  ATTR_VALUE = #{attrValue,jdbcType=VARCHAR}

select ATTR_VALUE from mb_attr_value
WHERE ATTR_KEY = #{attrKey,jdbcType=VARCHAR}
AND  ATTR_VALUE = #{attrValue,jdbcType=VARCHAR}
