mapper.select:
select *
from mb_event_attr
where EVENT_TYPE = #{eventType,jdbcType=VARCHAR}

  AND SEQ_NO = #{seqNo,jdbcType=VARCHAR}

AND COLUMN_STATUS !='W'

select MAX(SEQ_NO+0)
from mb_event_attr
where EVENT_TYPE = #{prodType,jdbcType=VARCHAR}

mapper.delete:
delete from mb_event_attr
where EVENT_TYPE = #{eventType,jdbcType=VARCHAR}

    AND SEQ_NO = #{seqNo,jdbcType=VARCHAR}

delete from mb_event_attr
where EVENT_TYPE = #{eventType,jdbcType=VARCHAR}
   AND ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR}

mapper.update:
update mb_event_attr


    ASSEMBLE_TYPE = #{assembleType,jdbcType=VARCHAR},


    ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR},


    ATTR_VALUE = #{attrValue,jdbcType=VARCHAR},


    ASSEMBLE_RULE = #{assembleRule,jdbcType=VARCHAR},


    COMPANY = #{company,jdbcType=VARCHAR}


where EVENT_TYPE = #{eventType,jdbcType=VARCHAR}
AND SEQ_NO = #{seqNo,jdbcType=DECIMAL}

update mb_event_attr


    ASSEMBLE_TYPE = #{assembleType,jdbcType=VARCHAR},


    ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR},


    ATTR_VALUE = #{attrValue,jdbcType=VARCHAR},


    COMPANY = #{company,jdbcType=VARCHAR}


where EVENT_TYPE = #{eventType,jdbcType=VARCHAR}
AND ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR}

update mb_event_attr


    ATTR_VALUE = #{attrValue,jdbcType=VARCHAR}


where EVENT_TYPE = #{eventType,jdbcType=VARCHAR}
AND ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR}

mapper.select:
select *
from mb_event_attr
where EVENT_TYPE = #{eventType,jdbcType=VARCHAR}

  AND ASSEMBLE_TYPE = #{assembleType,jdbcType=VARCHAR}


  AND ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR}

order by SEQ_no
