mapper.select:
select *
from mb_event_class
where PARENT_EVENT_CLASS LIKE #{parentEventClass,jdbcType=VARCHAR}

mapper.update:
update mb_event_class


    EVENT_CLASS_DESC = #{eventClassDesc,jdbcType=VARCHAR},


    EVENT_CLASS_LEVEL = #{eventClassLevel,jdbcType=VARCHAR},


    PARENT_EVENT_CLASS = #{parentEventClass,jdbcType=VARCHAR},


    COMPANY = #{company,jdbcType=VARCHAR}


where EVENT_CLASS = #{eventClass,jdbcType=VARCHAR}
