mapper.select:
select *
from mb_event_link
where PROD_TYPE = #{prodType,jdbcType=VARCHAR}

    AND ORIG_PROD_TYPE = #{origProdType,jdbcType=VARCHAR}


    AND ORIG_EVENT_TYPE = #{origEventType,jdbcType=VARCHAR}


    AND LINK_PROD_TYPE = #{linkProdType,jdbcType=VARCHAR}


    AND LINK_EVENT_TYPE = #{linkEventType,jdbcType=VARCHAR}

mapper.delete:
delete from mb_event_link
where PROD_TYPE = #{prodType,jdbcType=VARCHAR}

    AND ORIG_PROD_TYPE = #{origProdType,jdbcType=VARCHAR}


    AND ORIG_EVENT_TYPE = #{origEventType,jdbcType=VARCHAR}


    AND LINK_PROD_TYPE = #{linkProdType,jdbcType=VARCHAR}


    AND LINK_EVENT_TYPE = #{linkEventType,jdbcType=VARCHAR}

mapper.update:
update mb_event_link


    LINK_CONDITION = #{linkCondition,jdbcType=VARCHAR},


    STATUS = #{status,jdbcType=VARCHAR},


    COMPANY = #{company,jdbcType=VARCHAR}


where PROD_TYPE = #{prodType,jdbcType=VARCHAR}
    AND ORIG_PROD_TYPE = #{origProdType,jdbcType=VARCHAR}
    AND ORIG_EVENT_TYPE = #{origEventType,jdbcType=VARCHAR}
    AND LINK_PROD_TYPE = #{linkProdType,jdbcType=VARCHAR}
    AND LINK_EVENT_TYPE = #{linkEventType,jdbcType=VARCHAR}
