mapper.select:
select *
from mb_event_nodes
where PROD_TYPE = #{prodType,jdbcType=VARCHAR}

    AND EVENT_TYPE = #{eventType,jdbcType=VARCHAR}


    AND EVENT_NODE = #{eventNode,jdbcType=VARCHAR}


    AND METHOD_TYPE = #{methodType,jdbcType=VARCHAR}

mapper.delete:
delete from mb_event_nodes
where PROD_TYPE = #{prodType,jdbcType=VARCHAR}

    AND EVENT_TYPE = #{eventType,jdbcType=VARCHAR}


    AND EVENT_NODE = #{eventNode,jdbcType=VARCHAR}


    AND METHOD_TYPE = #{methodType,jdbcType=VARCHAR}

mapper.update:
update mb_event_nodes


    EVENT_NODE_DESC = #{eventNodeDesc,jdbcType=VARCHAR},


    ALIAS = #{alias,jdbcType=VARCHAR},


    CLAZZ = #{clazz,jdbcType=VARCHAR},


    METHOD_NAME = #{methodName,jdbcType=VARCHAR},


    ARGS = #{args,jdbcType=VARCHAR},


    ARG_CLAZZ = #{argClazz,jdbcType=VARCHAR},


    VAR = #{var,jdbcType=VARCHAR}


where PROD_TYPE = #{prodType,jdbcType=VARCHAR}
    AND EVENT_TYPE = #{eventType,jdbcType=VARCHAR}
    AND EVENT_NODE = #{eventNode,jdbcType=VARCHAR}
    AND METHOD_TYPE = #{methodType,jdbcType=VARCHAR}
