mapper.select:
select *
from mb_event_part
where EVENT_TYPE = #{eventType,jdbcType=VARCHAR}

  AND ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR}


  AND ATTR_KEY = #{attrKey,jdbcType=VARCHAR}

AND COLUMN_STATUS !='W'

select * from mb_event_part
where EVENT_TYPE = #{eventType,jdbcType=VARCHAR}
AND ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR}
AND COLUMN_STATUS!='N'

select * from mb_event_part
where EVENT_TYPE = #{eventType,jdbcType=VARCHAR}
AND ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR}
AND COLUMN_STATUS!='Y'

mapper.delete:
delete from mb_event_part
where EVENT_TYPE = #{eventType,jdbcType=VARCHAR}

    AND ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR}


    AND ATTR_KEY = #{attrKey,jdbcType=VARCHAR}

mapper.update:
update mb_event_part


    ATTR_VALUE = #{attrValue,jdbcType=VARCHAR}


where EVENT_TYPE = #{eventType,jdbcType=VARCHAR}
AND ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR}
AND ATTR_KEY = #{attrKey,jdbcType=VARCHAR}
