mapper.select:
select *
from mb_event_part_relation
where EVENT_TYPE = #{eventType,jdbcType=VARCHAR}

    AND ASSEMBLE_TYPE = #{assembleType,jdbcType=VARCHAR}


    AND ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR}

mapper.delete:
delete from mb_event_part_relation
where EVENT_TYPE = #{eventType,jdbcType=VARCHAR}

    AND ASSEMBLE_TYPE = #{assembleType,jdbcType=VARCHAR}


    AND ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR}

mapper.update:
update mb_event_part_relation


    PART_DESC = #{partDesc,jdbcType=VARCHAR},


    STATUS = #{status,jdbcType=VARCHAR}


where EVENT_TYPE = #{eventType,jdbcType=VARCHAR}
    AND ASSEMBLE_TYPE = #{assembleType,jdbcType=VARCHAR}
    AND ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR}
