mapper.select:
select *
from mb_part_attr
where PART_TYPE = #{partType,jdbcType=VARCHAR}

  AND ATTR_KEY = #{attrKey,jdbcType=VARCHAR}

AND COLUMN_STATUS !='W'

mapper.delete:
delete from mb_part_attr
where PART_TYPE = #{partType,jdbcType=VARCHAR}

    AND ATTR_KEY = #{attrKey,jdbcType=VARCHAR}

mapper.update:
update mb_part_attr


    ATTR_VALUE = #{attrValue,jdbcType=VARCHAR},


    COMPANY = #{company,jdbcType=VARCHAR}


where PART_TYPE = #{partType,jdbcType=VARCHAR}
AND ATTR_KEY = #{attrKey,jdbcType=VARCHAR}

mapper.select:
select * from mb_part_attr
where PART_TYPE = #{partType,jdbcType=VARCHAR}
and COLUMN_STATUS!='N'

select * from mb_part_attr
where PART_TYPE = #{partType,jdbcType=VARCHAR}
and COLUMN_STATUS!='Y'
