mapper.select:
select * from  mb_part_class
where PARENT_PART_CLASS = #{partClass,jdbcType=VARCHAR}
AND COLUMN_STATUS !='W'

mapper.update:
update mb_part_class


    PART_CLASS_DESC = #{partClassDesc,jdbcType=VARCHAR},


    PART_CLASS_LEVEL = #{partClassLevel,jdbcType=VARCHAR},


    PARENT_PART_CLASS = #{parentPartClass,jdbcType=VARCHAR},


    COMPANY = #{company,jdbcType=VARCHAR}


where PART_CLASS = #{partClass,jdbcType=VARCHAR}
