mapper.select:
select *
from mb_prod_class
where PARENT_PROD_CLASS LIKE #{parentProdClass,jdbcType=VARCHAR}

mapper.update:
update mb_prod_class


    PROD_CLASS_DESC = #{prodClassDesc,jdbcType=VARCHAR},


    PROD_CLASS_LEVEL = #{prodClassLevel,jdbcType=VARCHAR},


    PARENT_PROD_CLASS = #{parentProdClass,jdbcType=VARCHAR},


    COMPANY = #{company,jdbcType=VARCHAR}


where PROD_CLASS = #{prodClass,jdbcType=VARCHAR}

mapper.select:
select * from  mb_prod_class
where PROD_CLASS_LEVEL = #{prodClassLevel,jdbcType=VARCHAR}

  AND PROD_CLASS = #{prodClass,jdbcType=VARCHAR}

select * from  mb_prod_class
where    PARENT_PROD_CLASS =#{parentProdClass,jdbcType=VARCHAR}
AND PROD_CLASS_LEVEL = #{prodClassLevel,jdbcType=VARCHAR}

select * from  mb_prod_class
where PROD_CLASS = #{prodClass,jdbcType=VARCHAR}
and PROD_CLASS_LEVEL = '1'
