mapper.select:
select *
from mb_prod_define
where PROD_TYPE = #{prodType,jdbcType=VARCHAR}

  AND SEQ_NO = #{seqNo,jdbcType=VARCHAR}

AND COLUMN_STATUS !='W'

select MAX(SEQ_NO+0)
from mb_prod_define
where PROD_TYPE = #{prodType,jdbcType=VARCHAR}

mapper.delete:
delete from mb_prod_define
where PROD_TYPE = #{prodType,jdbcType=VARCHAR}

    AND SEQ_NO = #{seqNo,jdbcType=VARCHAR}

delete from mb_prod_define
where PROD_TYPE = #{prodType,jdbcType=VARCHAR}
AND ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR}

mapper.update:
update mb_prod_define


    ASSEMBLE_TYPE = #{assembleType,jdbcType=VARCHAR},


    ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR},


    EVENT_DEFAULT = #{eventDefault,jdbcType=VARCHAR},


    ATTR_KEY = #{attrKey,jdbcType=VARCHAR},


    ATTR_VALUE = #{attrValue,jdbcType=VARCHAR},


    STATUS = #{status,jdbcType=VARCHAR},


    COMPANY = #{company,jdbcType=VARCHAR}


where PROD_TYPE = #{prodType,jdbcType=VARCHAR}
AND SEQ_NO = #{seqNo,jdbcType=VARCHAR}

update mb_prod_define


    ASSEMBLE_TYPE = #{assembleType,jdbcType=VARCHAR},


    ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR},


    ATTR_KEY = #{attrKey,jdbcType=VARCHAR},


    ATTR_VALUE = #{attrValue,jdbcType=VARCHAR},


    STATUS = #{status,jdbcType=VARCHAR},


    COMPANY = #{company,jdbcType=VARCHAR}


where PROD_TYPE = #{prodType,jdbcType=VARCHAR}
AND ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR}

mapper.select:
select *
from mb_prod_define
WHERE
PROD_TYPE = #{prodType,jdbcType=VARCHAR}

  AND ASSEMBLE_TYPE = #{assembleType,jdbcType=VARCHAR}


  AND ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR}

order by SEQ_NO

select *
from mb_prod_define
WHERE
PROD_TYPE = #{prodType,jdbcType=VARCHAR}
AND   ATTR_KEY = #{attrKey,jdbcType=VARCHAR}
order by SEQ_NO
