mapper.select:
select *
from mb_prod_relation_define
where PROD_TYPE = #{prodType,jdbcType=VARCHAR}

    AND SUB_PROD_TYPE = #{subProdType,jdbcType=VARCHAR}


    AND EVENT_TYPE = #{eventType,jdbcType=VARCHAR}


    AND ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR}

SELECT DISTINCT EVENT_TYPE,ASSEMBLE_TYPE,ASSEMBLE_ID,STATUS
FROM mb_prod_relation_define
where PROD_TYPE = #{prodType,jdbcType=VARCHAR}

mapper.delete:
delete from mb_prod_relation_define
where PROD_TYPE = #{prodType,jdbcType=VARCHAR}

    AND SUB_PROD_TYPE = #{subProdType,jdbcType=VARCHAR}


    AND EVENT_TYPE = #{eventType,jdbcType=VARCHAR}


    AND ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR}

mapper.update:
update mb_prod_relation_define


    ASSEMBLE_TYPE = #{assembleType,jdbcType=VARCHAR},


    STATUS = #{status,jdbcType=VARCHAR}


where PROD_TYPE = #{prodType,jdbcType=VARCHAR}
    AND SUB_PROD_TYPE = #{subProdType,jdbcType=VARCHAR}
    AND EVENT_TYPE = #{eventType,jdbcType=VARCHAR}
    AND ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR}
