mapper.select:
select *
from mb_prod_run_rule
where EVENT_TYPE = #{eventType,jdbcType=VARCHAR}

    AND SEQ_NO = #{seqNo,jdbcType=INTEGER}

mapper.delete:
delete from mb_prod_run_rule
where EVENT_TYPE = #{eventType,jdbcType=VARCHAR}

    AND SEQ_NO = #{seqNo,jdbcType=INTEGER}

mapper.update:
update mb_prod_run_rule


    ASSEMBLE_ID = #{assembleId,jdbcType=VARCHAR},


    ASSEMBLE_TYPE = #{assembleType,jdbcType=VARCHAR},


    ASSEMBLE_RULE = #{assembleRule,jdbcType=VARCHAR},


    ASSEMBLE_ARGS = #{assembleArgs,jdbcType=VARCHAR},


    REMARK = #{remark,jdbcType=VARCHAR}


where EVENT_TYPE = #{eventType,jdbcType=VARCHAR}
    AND SEQ_NO = #{seqNo,jdbcType=INTEGER}
