mapper.select:
select *
from PARA_DIFFERENCE_CHECK_PUBLISH
where REQ_NO = #{reqNo,jdbcType=VARCHAR}
AND TABLE_FULL_NAME = #{tableFullName,jdbcType=VARCHAR}
ORDER BY  TRAN_TIMESTAMP

select *
from PARA_DIFFERENCE_CHECK_PUBLISH
where REQ_NO = #{reqNo,jdbcType=VARCHAR}
      AND TABLE_FULL_NAME = #{tableFullName,jdbcType=VARCHAR}
      AND PRIMARY_KEYVALUE = #{primaryKeyvalue,jdbcType=VARCHAR}
      AND OPERATE_TYPE = #{operateType,jdbcType=VARCHAR}

      AND SERIES_NUM = #{seriesNum,jdbcType=DECIMAL}

mapper.delete:
delete from PARA_DIFFERENCE_CHECK_PUBLISH
where REQ_NO = #{reqNo,jdbcType=VARCHAR}
  AND TABLE_FULL_NAME = #{tableFullName,jdbcType=VARCHAR}
  AND PRIMARY_KEYVALUE = #{primaryKeyvalue,jdbcType=VARCHAR}

      AND SERIES_NUM = #{seriesNum,jdbcType=DECIMAL}

delete from PARA_DIFFERENCE_CHECK_PUBLISH
where REQ_NO = #{reqNo,jdbcType=VARCHAR}
AND TABLE_FULL_NAME = #{tableFullName,jdbcType=VARCHAR}

mapper.select:
select *
from PARA_DIFFERENCE_CHECK_PUBLISH
where TRAN_TIMESTAMP = #{tranTimestamp,jdbcType=VARCHAR}

    AND SERIES_NUM = #{seriesNum,jdbcType=DECIMAL}

mapper.delete:
delete from PARA_DIFFERENCE_CHECK_PUBLISH
where TRAN_TIMESTAMP = #{tranTimestamp,jdbcType=VARCHAR}

    AND SERIES_NUM = #{seriesNum,jdbcType=DECIMAL}

mapper.update:
update PARA_DIFFERENCE_CHECK_PUBLISH


    REQ_NO = #{reqNo,jdbcType=VARCHAR},


    TABLE_FULL_NAME = #{tableFullName,jdbcType=VARCHAR},


    PRIMARY_KEYVALUE = #{primaryKeyvalue,jdbcType=VARCHAR},


    KEY_VALUE = #{keyValue,jdbcType=BLOB},


    OPERATE_TYPE = #{operateType,jdbcType=VARCHAR},


    DATA_DUI = #{dataDui,jdbcType=BLOB},


    OLDDATA_UPD = #{olddataUpd,jdbcType=BLOB}


where SERIES_NUM = #{seriesNum,jdbcType=DECIMAL}
AND TRAN_TIMESTAMP = #{tranTimestamp,jdbcType=VARCHAR}
