mapper.select:
select *
from PARA_MODULE_ORG
where MODULE_ID = #{moduleId,jdbcType=VARCHAR}

    AND SYSTEM_ID = #{systemId,jdbcType=VARCHAR}

mapper.delete:
delete from PARA_MODULE_ORG
where MODULE_ID = #{moduleId,jdbcType=VARCHAR}

    AND SYSTEM_ID = #{systemId,jdbcType=VARCHAR}

mapper.update:
update PARA_MODULE_ORG


    MODULE_NAME = #{moduleName,jdbcType=VARCHAR},


    MODULE_DESC = #{moduleDesc,jdbcType=VARCHAR}


where MODULE_ID = #{moduleId,jdbcType=VARCHAR}
    AND SYSTEM_ID = #{systemId,jdbcType=VARCHAR}
