mapper.select:
select *
from PARA_NAMESPACE_ORG
where TRANSACTION_ID = #{transactionId,jdbcType=VARCHAR}

  AND SYSTEM_ID = #{systemId,jdbcType=VARCHAR}

mapper.delete:
delete from PARA_NAMESPACE_ORG
where TRANSACTION_ID = #{transactionId,jdbcType=VARCHAR}

  AND SYSTEM_ID = #{systemId,jdbcType=VARCHAR}

mapper.select:
select

  TRANSACTION_ID pk_value, TRANSACTION_ID || ' ' || TRANSACTION_DESC pk_desc


  TRANSACTION_ID pk_value, concat(TRANSACTION_ID,' ', TRANSACTION_DESC) pk_desc

FROM PARA_NAMESPACE_ORG
WHERE SYSTEM_ID = #{systemId,jdbcType=VARCHAR}

AND MODULE_ID = #{moduleId,jdbcType=VARCHAR}

select
TRANSACTION_ID pk_value, TRANSACTION_ID || ' ' || TRANSACTION_DESC pk_desc FROM PARA_NAMESPACE_ORG WHERE SYSTEM_ID = #{systemId,jdbcType=VARCHAR}
AND MODULE_ID = #{moduleId,jdbcType=VARCHAR}
AND SYSTEM_ID || '_' || TRANSACTION_ID not in (select TRANSACTION_ID FROM PARA_CIRCLE_FLOW where CURRENT_STATUS = '3' OR CURRENT_STATUS = '2')
TRANSACTION_ID pk_value, concat(TRANSACTION_ID,' ', TRANSACTION_DESC) pk_desc FROM PARA_NAMESPACE_ORG WHERE SYSTEM_ID = #{systemId,jdbcType=VARCHAR}
AND MODULE_ID = #{moduleId,jdbcType=VARCHAR}
AND concat(SYSTEM_ID , '_' , TRANSACTION_ID) not in (select TRANSACTION_ID FROM PARA_CIRCLE_FLOW where CURRENT_STATUS = '3' OR CURRENT_STATUS = '2')
order by TRANSACTION_ID

select * FROM PARA_NAMESPACE_ORG
  WHERE SYSTEM_ID = #{systemId,jdbcType=VARCHAR}
  AND TRANSACTION_ID = #{transactionId,jdbcType=VARCHAR}

      AND MODULE_ID = #{moduleId,jdbcType=VARCHAR}


  AND SYSTEM_ID || '_' || TRANSACTION_ID not in


  AND concat(SYSTEM_ID , '_' , TRANSACTION_ID) not in

  (select TRANSACTION_ID FROM PARA_CIRCLE_FLOW where CURRENT_STATUS != '5'
  And CURRENT_STATUS != '4')
