mapper.select:
select *
from PARA_TABLE_FIELDS
where TABLE_NAME = #{tableName,jdbcType=VARCHAR}

  AND COLUMN_NAME = #{columnName,jdbcType=VARCHAR}

order by IS_PRIMARY DESC,IS_NULL

select *
from PARA_TABLE_FIELDS
where TABLE_NAME = #{tableName,jdbcType=VARCHAR}

    AND COLUMN_NAME = #{columnName,jdbcType=VARCHAR}

select count(*)
from PARA_TABLE_FIELDS
where IS_PRIMARY = 'Y' AND TABLE_NAME=#{tableName,jdbcType=VARCHAR}

mapper.delete:
delete from PARA_TABLE_FIELDS
where TABLE_NAME = #{tableName,jdbcType=VARCHAR}

    AND COLUMN_NAME = #{columnName,jdbcType=VARCHAR}

mapper.update:
update PARA_TABLE_FIELDS


    IS_NULL = #{isNull,jdbcType=VARCHAR},


    IS_PRIMARY = #{isPrimary,jdbcType=VARCHAR}


where TABLE_NAME = #{tableName,jdbcType=VARCHAR}
    AND COLUMN_NAME = #{columnName,jdbcType=VARCHAR}

mapper.select:
SELECT COLUMN_NAME FROM PARA_TABLE_FIELDS
where TABLE_NAME = #{tableName,jdbcType=VARCHAR}
and IS_PRIMARY = 'Y'

SELECT COLUMN_NAME FROM PARA_TABLE_FIELDS
where TABLE_NAME = #{tableName,jdbcType=VARCHAR}
and IS_PRIMARY != 'Y'

select FIELDS_DESC
from PARA_TABLE_FIELDS
where TABLE_NAME = #{tableName,jdbcType=VARCHAR}
